INSERT INTO `jc_comment_ext` VALUES ('1255', '', '中式快餐，味道一般，花样时常有更新，但份量似乎越来越少了点。虽说加饭加面这些都免费，但似乎不太情愿的样子。服务算中等', null);
INSERT INTO `jc_comment_ext` VALUES ('1256', '', '感觉还好，烤翅好吃，不过就是比重庆的贵一块，', null);
