INSERT INTO `jc_comment_ext` VALUES ('1258', '', '主要便宜、味道也可以、适合上班族牛排套餐很划算的说', null);
INSERT INTO `jc_comment_ext` VALUES ('1259', '', '快餐店儿嘛，还好，不用多说，但是我每次去火车站的话，只要是吃饭就只去乡村基~~~', null);
INSERT INTO `jc_comment_ext` VALUES ('1260', '', '在市中心找吃的很麻烦，乡村基不错，价格便宜量也足，要是我在那上班，一定天天去吃，首推卤肉饭！！！', null);
INSERT INTO `jc_comment_ext` VALUES ('1261', '', '支持他们的酸菜肉丝米线哈，就是没得以前在重庆店子上吃的分量夯实哈。', null);
